import React from "react";
import "./FilterButtons.css";

const FilterButtons = ({
  currentFilter,
  onFilterChange,
  onClearCompleted,
  onClearAll,
  hasCompleted,
  hasAny,
}) => {
  const filters = [
    { key: "all", label: "üìã All", count: null },
    { key: "active", label: "‚è≥ Active", count: null },
    { key: "completed", label: "‚úÖ Completed", count: null },
  ];

  return (
    <div className="filter-section">
      <div className="filter-buttons">
        {filters.map((filter) => (
          <button
            key={filter.key}
            className={`filter-button ${
              currentFilter === filter.key ? "active" : ""
            }`}
            onClick={() => onFilterChange(filter.key)}
          >
            {filter.label}
          </button>
        ))}
      </div>

      <div className="action-buttons">
        {hasCompleted && (
          <button
            className="action-button clear-completed"
            onClick={onClearCompleted}
          >
            üßπ Clear Completed
          </button>
        )}
        {hasAny && (
          <button className="action-button clear-all" onClick={onClearAll}>
            üóëÔ∏è Clear All
          </button>
        )}
      </div>
    </div>
  );
};

export default FilterButtons;
