import React from "react";
import "./TodoItem.css";

const TodoItem = ({ todo, onToggle, onDelete }) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

    if (diffInHours < 1) return "Just now";
    if (diffInHours < 24) return `${diffInHours}h ago`;

    return date.toLocaleDateString("en-US", {
      month: "short",
      day: "numeric",
    });
  };

  return (
    <div
      className={`todo-item ${todo.completed ? "completed" : ""} ${
        todo.urgency
      }`}
    >
      <div className="todo-main">
        <button
          className="toggle-button"
          onClick={onToggle}
          aria-label={
            todo.completed ? "Mark as incomplete" : "Mark as complete"
          }
        >
          <div className={`checkbox ${todo.completed ? "checked" : ""}`}>
            {todo.completed && <div className="checkmark"></div>}
          </div>
        </button>

        <div className="todo-content">
          <span className="todo-text">{todo.text}</span>
          <div className="todo-meta">
            <span className={`urgency-badge ${todo.urgency}`}>
              {todo.urgency === "urgent" ? "Urgent" : "Non-Urgent"}
            </span>
            <span className="category-badge">{todo.category}</span>
            <span className="date-badge">{formatDate(todo.createdAt)}</span>
          </div>
        </div>
      </div>

      <button
        className="delete-button"
        onClick={onDelete}
        aria-label="Delete task"
      >
        <div className="delete-icon"></div>
      </button>
    </div>
  );
};

export default TodoItem;
