import React, { useState, useEffect } from "react";
import "./App.css";
import TodoForm from "./components/TodoForm";
import TodoList from "./components/TodoList";
import FilterButtons from "./components/FilterButtons";

function App() {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState("all");

  // Load todos from localStorage on component mount
  useEffect(() => {
    const savedTodos = localStorage.getItem("react-todos");
    if (savedTodos) {
      setTodos(JSON.parse(savedTodos));
    }
  }, []);

  // Save todos to localStorage whenever todos change
  useEffect(() => {
    localStorage.setItem("react-todos", JSON.stringify(todos));
  }, [todos]);

  const addTodo = (text, priority = "medium") => {
    const newTodo = {
      id: Date.now(),
      text,
      priority,
      completed: false,
      createdAt: new Date().toISOString(),
    };
    setTodos([newTodo, ...todos]);
  };

  const toggleTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const clearCompleted = () => {
    setTodos(todos.filter((todo) => !todo.completed));
  };

  const clearAll = () => {
    if (window.confirm("Are you sure you want to clear all todos?")) {
      setTodos([]);
    }
  };

  const getFilteredTodos = () => {
    switch (filter) {
      case "active":
        return todos.filter((todo) => !todo.completed);
      case "completed":
        return todos.filter((todo) => todo.completed);
      default:
        return todos;
    }
  };

  const stats = {
    total: todos.length,
    completed: todos.filter((todo) => todo.completed).length,
    active: todos.filter((todo) => !todo.completed).length,
  };

  return (
    <div className="app">
      <div className="container">
        <header className="header">
          <h1>âœ¨ Modern TODO</h1>
          <p>Stay organized and productive</p>
        </header>

        <TodoForm onAddTodo={addTodo} />

        <div className="stats">
          <span>Total: {stats.total}</span>
          <span>Active: {stats.active}</span>
          <span>Completed: {stats.completed}</span>
        </div>

        <FilterButtons
          currentFilter={filter}
          onFilterChange={setFilter}
          onClearCompleted={clearCompleted}
          onClearAll={clearAll}
          hasCompleted={stats.completed > 0}
          hasAny={stats.total > 0}
        />

        <TodoList
          todos={getFilteredTodos()}
          onToggleTodo={toggleTodo}
          onDeleteTodo={deleteTodo}
        />

        {todos.length === 0 && (
          <div className="empty-state">
            <p>ðŸŽ¯ No todos yet. Add one above to get started!</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
