import React, { useState, useEffect } from "react";
import "./App.css";
import TodoForm from "./components/TodoForm";
import TodoList from "./components/TodoList";

function App() {
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState("all");

  // Load todos from localStorage on component mount
  useEffect(() => {
    const savedTodos = localStorage.getItem("nothing-todos");
    if (savedTodos) {
      setTodos(JSON.parse(savedTodos));
    }
  }, []);

  // Save todos to localStorage whenever todos change
  useEffect(() => {
    localStorage.setItem("nothing-todos", JSON.stringify(todos));
  }, [todos]);

  const addTodo = (text, urgency = "non-urgent", category = "personal") => {
    const newTodo = {
      id: Date.now(),
      text,
      urgency,
      category,
      completed: false,
      createdAt: new Date().toISOString(),
    };
    setTodos([newTodo, ...todos]);
  };

  const toggleTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const clearCompleted = () => {
    setTodos(todos.filter((todo) => !todo.completed));
  };

  const clearAll = () => {
    if (window.confirm("Clear all tasks?")) {
      setTodos([]);
    }
  };

  const getFilteredTodos = () => {
    switch (filter) {
      case "urgent":
        return todos.filter((todo) => todo.urgency === "urgent");
      case "non-urgent":
        return todos.filter((todo) => todo.urgency === "non-urgent");
      case "personal":
        return todos.filter((todo) => todo.category === "personal");
      case "work":
        return todos.filter((todo) => todo.category === "work");
      case "others":
        return todos.filter((todo) => todo.category === "others");
      case "completed":
        return todos.filter((todo) => todo.completed);
      case "active":
        return todos.filter((todo) => !todo.completed);
      default:
        return todos;
    }
  };

  const stats = {
    total: todos.length,
    completed: todos.filter((todo) => todo.completed).length,
    urgent: todos.filter((todo) => todo.urgency === "urgent" && !todo.completed)
      .length,
    personal: todos.filter(
      (todo) => todo.category === "personal" && !todo.completed
    ).length,
    work: todos.filter((todo) => todo.category === "work" && !todo.completed)
      .length,
    others: todos.filter(
      (todo) => todo.category === "others" && !todo.completed
    ).length,
  };

  return (
    <div className="app">
      <div className="app-container">
        {/* Left Column - Input */}
        <div className="left-panel">
          <header className="header">
            <div className="logo">
              <div className="dot"></div>
              <h1>Tasks</h1>
            </div>
            <p className="subtitle">Nothing but productivity</p>
          </header>

          <TodoForm onAddTodo={addTodo} />

          <div className="stats-grid">
            <div className="stat-item">
              <span className="stat-number">{stats.total}</span>
              <span className="stat-label">Total</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{stats.urgent}</span>
              <span className="stat-label">Urgent</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{stats.personal}</span>
              <span className="stat-label">Personal</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{stats.work}</span>
              <span className="stat-label">Work</span>
            </div>
          </div>

          <div className="actions">
            {stats.completed > 0 && (
              <button className="action-btn" onClick={clearCompleted}>
                Clear Completed
              </button>
            )}
            {stats.total > 0 && (
              <button className="action-btn danger" onClick={clearAll}>
                Clear All
              </button>
            )}
          </div>
        </div>

        {/* Right Column - Tasks Display */}
        <div className="right-panel">
          <div className="filter-tabs">
            <button
              className={`filter-tab ${filter === "all" ? "active" : ""}`}
              onClick={() => setFilter("all")}
            >
              All
            </button>
            <button
              className={`filter-tab ${filter === "urgent" ? "active" : ""}`}
              onClick={() => setFilter("urgent")}
            >
              Urgent
            </button>
            <button
              className={`filter-tab ${
                filter === "non-urgent" ? "active" : ""
              }`}
              onClick={() => setFilter("non-urgent")}
            >
              Non-Urgent
            </button>
            <div className="tab-divider"></div>
            <button
              className={`filter-tab ${filter === "personal" ? "active" : ""}`}
              onClick={() => setFilter("personal")}
            >
              Personal
            </button>
            <button
              className={`filter-tab ${filter === "work" ? "active" : ""}`}
              onClick={() => setFilter("work")}
            >
              Work
            </button>
            <button
              className={`filter-tab ${filter === "others" ? "active" : ""}`}
              onClick={() => setFilter("others")}
            >
              Others
            </button>
          </div>

          <TodoList
            todos={getFilteredTodos()}
            onToggleTodo={toggleTodo}
            onDeleteTodo={deleteTodo}
            filter={filter}
          />

          {getFilteredTodos().length === 0 && todos.length > 0 && (
            <div className="empty-state">
              <div className="empty-dot"></div>
              <p>No tasks in this category</p>
            </div>
          )}

          {todos.length === 0 && (
            <div className="empty-state">
              <div className="empty-dot"></div>
              <p>No tasks yet</p>
              <span>Add your first task to get started</span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
