import React, { useState } from "react";
import "./TodoForm.css";

const TodoForm = ({ onAddTodo }) => {
  const [text, setText] = useState("");
  const [urgency, setUrgency] = useState("non-urgent");
  const [category, setCategory] = useState("personal");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (text.trim()) {
      onAddTodo(text.trim(), urgency, category);
      setText("");
    }
  };

  return (
    <form className="todo-form" onSubmit={handleSubmit}>
      <div className="input-group">
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Add a new task..."
          className="task-input"
          maxLength={100}
        />
      </div>

      <div className="selectors">
        <div className="selector-group">
          <label className="selector-label">Priority</label>
          <div className="selector-buttons">
            <button
              type="button"
              className={`selector-btn ${urgency === "urgent" ? "active" : ""}`}
              onClick={() => setUrgency("urgent")}
            >
              Urgent
            </button>
            <button
              type="button"
              className={`selector-btn ${
                urgency === "non-urgent" ? "active" : ""
              }`}
              onClick={() => setUrgency("non-urgent")}
            >
              Non-Urgent
            </button>
          </div>
        </div>

        <div className="selector-group">
          <label className="selector-label">Category</label>
          <div className="selector-buttons">
            <button
              type="button"
              className={`selector-btn ${
                category === "personal" ? "active" : ""
              }`}
              onClick={() => setCategory("personal")}
            >
              Personal
            </button>
            <button
              type="button"
              className={`selector-btn ${category === "work" ? "active" : ""}`}
              onClick={() => setCategory("work")}
            >
              Work
            </button>
            <button
              type="button"
              className={`selector-btn ${
                category === "others" ? "active" : ""
              }`}
              onClick={() => setCategory("others")}
            >
              Others
            </button>
          </div>
        </div>
      </div>

      <button type="submit" className="add-button" disabled={!text.trim()}>
        Add Task
      </button>
    </form>
  );
};

export default TodoForm;
